import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

export const Title = styled.h1`
  font-size: 70px;
  color: #FFB9D1;
`;

export const Text = styled.p`
  font-size: 25px;
  color: black;
`;

export const Content = styled.div`
  max-width: 400px;
  margin: 20px
  padding-left: 20px
`;
