import styled from 'styled-components';

export const Container = styled.div`
  height: 100vh;
  width: 100vw;
  justify-content: space-between;
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

export const Content = styled.div`
  width: 100%;
  overflow-y: auto;
  overflow-x: hidden;
  height: 100%;
`;
